package com.longjingtech.ljhotelandroidapp.sys;import android.content.Context;import android.net.wifi.WifiInfo;import android.net.wifi.WifiManager;import android.nfc.Tag;import android.util.Log;import java.net.InetAddress;import java.net.NetworkInterface;import java.net.SocketException;import java.util.Enumeration;/** * Created by djstava on 15/1/28. */public class NetworkUtils {    private static final String TAG = NetworkUtils.class.getSimpleName();    private Context context;    private WifiManager wifiManager;    private WifiInfo wifiInfo;    private String wifiMacAddress;    private String ethernetMacAddress;    public NetworkUtils(Context context) {        this.context = context;    }    public String getWifiMacAddress() {        wifiManager = (WifiManager)context.getSystemService(Context.WIFI_SERVICE);        wifiInfo = wifiManager.getConnectionInfo();        wifiMacAddress = wifiInfo.getMacAddress();        return wifiMacAddress;    }    public String getEthernetMacAddress() {        try {            Enumeration localEnumeration = NetworkInterface.getNetworkInterfaces();            while (localEnumeration.hasMoreElements()) {                NetworkInterface networkInterface = (NetworkInterface)localEnumeration.nextElement();                String interfaceName = networkInterface.getDisplayName();                if (interfaceName == null) {                    continue;                }                else if (interfaceName.equals("eth0")) {                    ethernetMacAddress = convertToMacAddress(networkInterface.getHardwareAddress());                    if (ethernetMacAddress != null && ethernetMacAddress.startsWith("0:")) {                        ethernetMacAddress = "0" + ethernetMacAddress;                    }                    break;                }            }        } catch (SocketException e) {            e.printStackTrace();        }        Log.e(TAG,"=====djstava eth mac: " + ethernetMacAddress);        return ethernetMacAddress;    }    private static String convertToMacAddress(byte[] mac) {        StringBuilder stringBuilder = new StringBuilder();        for (int i = 0; i < mac.length ; i++) {            byte tmp = mac[i];            int value = 0;            if (tmp >= 0 && tmp <= 16) {                value = tmp;                stringBuilder.append("0" + Integer.toHexString(value));            }else if (tmp > 16) {                value = tmp;                stringBuilder.append(Integer.toHexString(value));            }else {                value = 256 + tmp;                stringBuilder.append(Integer.toHexString(value));            }            if (i != mac.length - 1) {                stringBuilder.append(":");            }        }        return stringBuilder.toString();    }}